definition.script:
pipeline {
    agent any

    tools {
        nodejs "NodeJS"  // Use the NodeJS name configured in Jenkins
    }

    stages {
        stage('Checkout') {
            steps {
                // Check out the code from the Git repository
                git branch: 'main', url: 'https://github.com/R13725056/Team_Buying.git'
            }
        }

        stage('Debug: List Workspace Contents') {
            steps {
                // List contents of the workspace to see the directory structure
                sh 'ls -R'
            }
        }

        stage('Install Dependencies') {
            steps {
                dir('front-end') {  // Changed from 'front-end' to 'frond-end'
                    sh 'npm install'
                }
            }
        }

        stage('Run Tests') {
            steps {
                dir('front-end') {  // Changed from 'front-end' to 'frond-end'
                    sh 'npm test'
                }
            }
        }

        stage('Build') {
            steps {
                dir('front-end') {  // Changed from 'front-end' to 'frond-end'
                    sh 'npm run build'
                }
            }
        }
    }

    post {
        always {
            // Clean up workspace to save disk space
            cleanWs()
        }
        success {
            // Send notification or perform other tasks on success
            echo 'Build and test completed successfully.'
        }
        failure {
            // Handle failure scenarios, e.g., send failure notification
            echo 'Build or test failed.'
        }
    }
}
